'use strict'

/**
 * 惰性单例模式
 * @param fn
 * @returns {Function}
 */

module.exports = (func) => {
    let instance = null

    if (typeof func !== "function") {
        throw new Error("signleton args must be function")
    }

    return function (...args) {
        if (!instance) {
            instance = func(...args)
        }
        return instance
    }
}